import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./All.css"
import ClimbingBoxLoader from "react-spinners/ClimbingBoxLoader";




const Home = () => {
    const [magzaListe, setMagzaListe] = useState([])


    const film_img = "https://www.cheapshark.com"

    useEffect(() => {
        const api_link = "https://www.cheapshark.com/api/1.0/stores"
        fetch(api_link)
            .then(fl => fl.json())
            .then(fl => {
                setMagzaListe(fl)

            })
    }, [])


    return (
        <div className="col-12 row row-cols-2 row-cols-md-6 g-2">
            <ClimbingBoxLoader>
                {
                    magzaListe.map(fl =>
                        <div>

                            <Link to={`/${fl.storeID}/${fl.storeName}/Deal`}>
                                <img src={film_img + fl.images.logo} width="180px" />
                            </Link>

                            <div>

                            </div>
                        </div>

                    )
                }
                </ClimbingBoxLoader>
        </div>
    )
}



export default Home;
