import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import ClimbingBoxLoader from "react-spinners/ClimbingBoxLoader";
import "./All.css"


const Deal = () => {
    let { storeId, storeName } = useParams();
    const [fırsatListe, setFırsatListe] = useState([])
    const [loading, setLoading] = useState(false);


    useEffect(() => {
        const api_link = `https://www.cheapshark.com/api/1.0/deals?storeID=${storeId}`
        setLoading(true)
        fetch(api_link)
            .then(fl => fl.json())
            .then(fl => {
                setFırsatListe(fl)
                setLoading(false)
            })
    }, [storeId])

    useEffect(() => {
        setLoading(true)
        setTimeout(() => {
            setLoading(false)
        },)
    }, []);


    return (




        <div className="container text-center">
            {loading ?

                <ClimbingBoxLoader className="orta"




                    color={"red"}
                    loading={loading}
                    size={10}


                />

                :

                <Link to="" style={{ textDecoration: 'none', color: "black" }}>
                    <h1>{storeName} Fırsatları</h1>
                    {
                        fırsatListe.map(fl =>
                            <div>
                                {fl.title}
                            </div>
                        )

                    }
                </Link>
            }
            <Link to={"/"}><button className="btn btn-outline-dark">Geri</button></Link>
        </div>

    )

}


export default Deal;
