import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import "./All.css"


const Home = () =>{
    let {storeId,storeName} = useParams();
    const [magzaListe,setMagzaListe] = useState([])

    const film_img="https://www.cheapshark.com"

    useEffect(() =>{
        const api_link="https://inshorts.deta.dev/news?category=sports"
        fetch(api_link)
        .then(fl=>fl.json())
        .then(fl=>{
            setMagzaListe(fl.data)
        })
    },[])

    return(
        <div className="col-12 row row-cols-2 row-cols-md-6 g-2">
            {
                magzaListe.map(fl=>
                    <div>
                        <Link to="/:storeId/:storeName/firsatlar"><img src={film_img + fl.images.logo} width="180px" /></Link>
                        <div>
                            <h1>
                                {fl.author}
                            </h1>
                        </div>
                    </div>
                    )
            }

        </div>
    )
}

export default Home;
