import { Link, useParams } from "react-router-dom";
import PuffLoader from "react-spinners/PuffLoader";


const Deal = () =>{
    let { storeId, storeName } = useParams();
    const [fırsatListe,setFırsatListe] = useState([])
    const [loading, setLoading] = useState(false);


    useEffect(() =>{
        const api_link=`https://www.cheapshark.com/api/1.0/deals?${storeId}`
        setLoading(true)
        fetch(api_link)
        .then(fl=>fl.json())
        .then(fl=>{
            setFırsatListe(fl)
            setLoading(false)
        })
    })

    useEffect(() => {
        setLoading(true)
        setTimeout(() => {
            setLoading(false)
        },)
    }, []);


    return(




        <div className="container text-center">
            {loading ?

