import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Deal = () =>{
    let {storeId} = useParams();
    const [fırsatListe,setFırsatListe] = useState([])

    useEffect(() =>{
        const api_link=`https://www.cheapshark.com/api/1.0/deals?${storeId}`
        fetch(api_link)
        .then(fl=>fl.json())
        .then(fl=>{
            setFırsatListe(fl)
        })
    })


    return(
        <div className="col-12 row row-cols-2 row-cols-md-6 g-2" style={{}}>
            {
                fırsatListe.map(fl=>
                    <div>
                        <h1>
                            {fl.title}
                        </h1>
                        <p>
                            {fl.normalPrice}
                        </p>
                    </div>
                    )
            }
        </div>
    )
}

export default Deal;
