import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";

const Deal = () =>{
    let { storeId, storeName } = useParams();
    const [fırsatListe,setFırsatListe] = useState([])

    useEffect(() =>{
        const api_link=`https://www.cheapshark.com/api/1.0/deals?${storeId}`
        fetch(api_link)
        .then(fl=>fl.json())
        .then(fl=>{
            setFırsatListe(fl)
        })
    })


    return(

        <div className="col-12" style={{ }}>
             <Link to={`/`}>
                <h1>{storeName} Fırsatları</h1>
             </Link>

        </div>
    )
}

export default Deal;
