import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const GameList = () =>{

    let {steamAppID} = useParams();
    const [game,setGame] = useState([])

    useEffect(() =>{
        const api_link=`https://www.cheapshark.com/api/1.0/games?title=batman&steamAppID=${steamAppID}&limit=60&exact=0`
        fetch(api_link)
        .then(g=>g.json())
        .then(g=>{
            setGame(g)
        })
    },[steamAppID])
return(
    <div>
        <p>
            {
                game.map(a=>
                    <div>
                        {a.external}
                    </div>
                    )
            }
        </p>
    </div>
)
}

export default GameList;
